import './App.css';
import FormInput from './components/FormInput';
import {useState} from "react";

function App() {
  const [values,setValues]= useState({
    username:"",
    firstname:"",
    lastname:"",
    email:"",
    password:"",
    confirmPassword:"",

  })
  const inputs= [
    {
      id:1,
      name:"username",
      type:"text",
      placeholder:"Username",
      errorMessage:"Username should be 6-12 characters!",
      pattern:"^{6,12}$",
      label:"Username"

    },
    {
      id:2,
      name:"firstname",
      type:"text",
      placeholder:"First Name",
      errorMessage:"First name is required. ",
      required:true,
      label:"First Name"

    },
    {
      id:3,
      name:"lastname",
      type:"text",
      placeholder:"Last name",
      errorMessage:"Last name is required.",
      required:true,
      label:"Last name"

    },
    {
      id:4,
      name:"email",
      type:"text",
      placeholder:"Email",
      errorMessage:"It should be valid email adress!",
      required:true,
      label:"Email"

    },
    {
      id:5,
      name:"password",
      type:"password",
      placeholder:"Password",
      errorMessage:"Pssword should be minimum 8 characters long and contain at least 1 uppercase letter, 1 lowercase letter, 1 number and one special character!",
      label:"Password"

    },
    {
      id:6,
      name:"confirmPassword",
      type:"password",
      placeholder:"Confirm Password",
      errorMessage:"Passwords don't match!",
      label:"Confirm Password"

    }
  ]

  const handleSubmit = (e)=>{
    e.preventDefault();

  }
  const onChange=(e)=>{
    setValues({...values,[e.target.name]:e.target.value});
  }
  console.log(values);
  return (
    <div className="app">
      <form onSubmit={handleSubmit}>
      <h1>Register</h1>
        {inputs.map((input)=>
        ( 
        <FormInput key={input.id} {...input} value={values[input.name]} onChange={onChange}/>


        ))}


        <button>Submit</button>

      </form>
    </div>
  );
}

export default App;
